import os
import subprocess

# Path to single locus tree file generated by iqtree
raw_gene_trees = "clean_single_locus_trees.nwk"

num_bootstrap_replicates = 100

number_trees = int(int(subprocess.check_output("cat clean_single_locus_trees.nwk | wc -l", shell=True).decode("utf-8").split("\n")[0]))
print(number_trees)

number_loci = int(int(subprocess.check_output("cat clean_single_locus_trees.nwk | wc -l", shell=True).decode("utf-8").split("\n")[0])/(num_bootstrap_replicates))
print(number_loci)

# Taxa expected to be present in each single locus tree
#set = {"droebachiensis","fragilis","pallidus","intermedius","purpuratus_SRR7211988","pulcherrimus_SRR5767283","nudus","franciscanus","depressus","purpuratus_SRR6281818","pulcherrimus_DRR107784"}
set = {"droebachiensis","fragilis","pallidus","intermedius","purpuratus","pulcherrimus"}
#set = {"droebachiensis","fragilis","pallidus","intermedius","purpuratus","pulcherrimus", "franciscanus"}

def create_input_file(output_file, program, num_reticulations, runs, processors, num_net_returned):
    with open(raw_gene_trees, "r") as f:
        raw_tree_list = f.readlines()

    reject_file = open("rejected_trees.nex", "w")
    
    filtered_gene_tree_lst = []
    for line in raw_tree_list:
        lst = []
        for n in set:
            lst.append(n in line)
        if all(lst) == True:
            filtered_gene_tree_lst.append(line)
        else:
            reject_file.write(line)
    
    reject_file.close()

    for count, tree in enumerate(filtered_gene_tree_lst):
        line = "Tree gt" + str(count) + "  = " + tree
        filtered_gene_tree_lst[count] = line

    gt_string = "("

    for n in range(0, number_loci + 1):
        if n == 0:
            new_string = r'{{gt{}-gt{}}}'.format(n * 100, (n * 100) + 99)
        elif n == number_loci:
            new_string = ")"
        else:
            new_string = r',{{gt{}-gt{}}}'.format(n * 100, (n * 100) + 99)

        gt_string += new_string

    with open(output_file,'a') as f2:
        line1 = "#NEXUS"
        line2 = "BEGIN TREES;"
        line3 = "END;"
        line4 = "BEGIN NETWORKS;"
        line5 = "NETWORK net = (pulcherrimus,(purpuratus,(intermedius,(pallidus,(droebachiensis,fragilis)))));"
        line6 = "END;"
        line7 = "BEGIN PHYLONET;"
        #line8 = '{} {} {} -x {} -pl {} -s net -di;'.format(program, gt_string, num_reticulations, runs, processors)
        line8 = '{} (all) {} -x {} -pl {} -s net -di;'.format(program, num_reticulations, runs, processors)
        line9 = "END;"
        f2.write(line1 + "\n" + "\n")
        f2.write(line2 + "\n")
        for tree in filtered_gene_tree_lst:
            f2.write(tree)
        f2.write(line3 + "\n" + "\n")
        f2.write(line4 + "\n")
        f2.write(line5 + "\n")
        f2.write(line6 + "\n" + "\n")
        f2.write(line7 + "\n")
        f2.write(line8 + "\n")
        f2.write(line9 + "\n")

def main():
    create_input_file("retic_0", "InferNetwork_ML", "0", "10", "20", "1")
    create_input_file("retic_1", "InferNetwork_ML", "1", "10", "20", "5")
    create_input_file("retic_2", "InferNetwork_ML", "2", "10", "20", "5")
    create_input_file("retic_3", "InferNetwork_ML_Bootstrap", "3", "10", "20", "5")

if __name__ == "__main__":
    main()